
public class DivideByZero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//what will be the output when you divide the number by zero
		
		
        System.out.println(9.0/0); //infinity
        System.out.println(12.4f/0); //infinity
        //System.out.println(0/0);//airthmatic exception
        System.out.println(0.0/0); //NAN
        System.out.println(124.4/0.0); //infinity
	}

}
